from django import formsfrom .models import AuctionListingclass AuctionListingForm(forms.ModelForm):    class Meta:        model = AuctionListing        fields = ['product', 'photo', 'description', 'starting_bid', 'date', 'start', 'end']        widgets = {'start': forms.DateTimeInput(attrs={'type': 'datetime-local', 'required': 'true'}),                   'end': forms.DateTimeInput(attrs={'type': 'datetime-local'}),           }class DescriptionForm(forms.ModelForm):    class Meta:        model = AuctionListing        fields = ['description', 'photo']        widgets = {'description': forms.Textarea(attrs={'rows': 5}),                   }